rule "NML - FW/Unattached - Normalize action field"

when
    has_field("action")
then
    let my_new_field = lowercase(to_string($message.action));
    set_field("action", my_new_field);
    set_field("parse", "NML - FW/Unattached - Normalize action field");
end

rule "Parse - Pi-hole Unattached - Parse pihole_action field"
when
    has_field("pihole_action") && grok (pattern: "%{PIHOLEACTION}",
         value: to_string($message.pihole_action)
         ).matches == true
then
//    set_field("pihole_action", "reply");
//    set_field("match_type", "custom-list");
    set_field("parse", "Parse - Pi-hole Unattached - Parse pihole_action field");
end

rule "NML - FW/OPN - Normalize protoname field"
when
    has_field("protoname")
then
    let my_new_field = lowercase(to_string($message.protoname));
    set_field("protoname",my_new_field);
    set_field("parse", "NML - FW/OPN - Normalize protoname field");
end

rule "10 - Parse - Pi-hole Lab - Split message into additional fields"
when
    grok (pattern: "%{PIHOLELAB}",
         value: to_string($message.message)
         ).matches == true
//In the event the value of the message field matches the custom PIHOLELAB grok pattern, we
//run the following code below
then
    let my_fields = grok(pattern: "%{NOTSPACE:pihole_action} %{IPORHOST:query_result_iporhost} %{WORD:pihole_context} %{IPORHOST:pihole_result}",
        value: to_string($message.message),
        only_named_captures: false);
        set_fields(my_fields);
    set_field("parse", "Parse - Pi-hole Lab - Split message into additional fields");
//We are using some built in grok patterns to help us parse the message and
//split it into pihole_action, iporhost, pihole_context, and pihole_result fields
end

rule "05 - Parse - Pi-hole Lab- Split message into initial fields"
when
    true
then
    let message_field = to_string($message.message);
    let fields = grok(pattern: "%{SYSLOGTIMESTAMP:timestamp} %{WORD:application_name}\\[%{POSINT:process_id}]\\: %{GREEDYDATA:message}", 
        value: message_field, 
        only_named_captures: true);
    set_fields(fields);
    set_field("parse", "parse - Pi-hole - Split pihole message into fields");

    //Fix the probably incorrect date formatted timestamp to make graylog happy
    //Normally we do not need to use this flex_parse_date function, however
    //sometimes it is required in limited exceptions where Graylog 
    //has something to say about unexpected timestamps
    let new_date = flex_parse_date(to_string($message.timestamp));
    set_field("timestamp", new_date);
    set_field("parse", "Parse - Pihole Lab - Split message into intial fields");
end

rule "GeoIP lookup: external ip"

when
    $message.internal_ip == false
then
    let geo = lookup("geoip", to_string($message.IP));
    set_field("ip_geo_location", geo["coordinates"]);
    set_field("ip_geo_country", geo["country"].iso_code);
    set_field("ip_geo_city", geo["city"].names.en);
    set_field("parse", "GeoIP lookup: external ip");
end

rule "Parse - Pi-hole Enhanced - Gravity tracking"
when
    $message.pihole_action == "gravity"
then
//    let my_match_type = to_string($message.pihole_action);
    let my_fields = grok(pattern: "%{WORD:match_type} %{WORD:pihole_action}",
    value: to_string($message.pihole_message),
    only_named_captures: true);
    set_fields(my_fields);
//    set_field("match_type", my_match_type);
    set_field("parse", "parse - Pi-hole Enhanced - Gravity tracking");
end

rule "Parse - Pi-hole Enhanced - Parsing pihole_action for additional fields"
when
    $message.pihole_action == "forwarded" OR $message.pihole_action == "reply" OR $message.pihole_action == "cached"
then
//    let my_match_type = to_string($message.pihole_action);
    let my_fields = grok(pattern: "%{IPORHOST} %{WORD:pihole_context} %{NOTSPACE:pihole_response}",
    value: to_string($message.pihole_result),
    only_named_captures: true);
    set_fields(my_fields);
//    set_field("match_type", my_match_type);
    set_field("parse", "parse - Pi-hole Enhanced - Parsing pihole_action for additional fields");
end

rule "Parse - Pi-hole Enhanced - Parsing pihole_action field"
when
    $message.pihole_action == "exactly" OR $message.pihole_action == "regex"
then
//    let my_match_type = to_string($message.pihole_action);
    let my_fields = grok(pattern: "%{WORD:match_type} %{WORD:pihole_action} %{HOSTNAME} %{WORD:pihole_context} %{GREEDYDATA:pihole_response}",
    value: to_string($message.pihole_message),
    only_named_captures: true);
    set_fields(my_fields);
//    set_field("match_type", my_match_type);
    set_field("parse", "Parse - Pi-hole Enhanced - Parsing pihole_action field");
end

rule "NML - Pi-hole Enhanced - Normalize pihole_action field"
when
    starts_with(to_string($message.pihole_action), "query")
then
    let my_fields = grok(pattern: "%{PIHOLEQUERYTYPE}",
    value: to_string($message.pihole_action),
    only_named_captures: true);
    set_fields(my_fields);
    set_field("pihole_action","query");
    set_field("parse","NML - Pi-hole Enhanced - Normalize pihole_action field");
end

rule "Parse - Pi-hole Enhanced - Parse for destination_ip"
when
    $message.pihole_action == "forwarded" && has_field("IP")
then
    let clean_ip = to_ip($message.IP);
    set_field("destination_ip", clean_ip);
    set_field("parse", "Parse - Pi-hole Enhanced - Parse for destination_ip");
end

rule "Parse - Pi-hole Enhanced - Parsing pihole_result field"
when
    has_field("pihole_result") &&
    grok (pattern: "%{WORD} %{IPORHOST} %{WORD:pihole_context} %{NOTSPACE:pihole_response}",
         value: to_string($message.message)
         ).matches == true
then
    let my_fields = grok (pattern: "%{WORD} %{IPORHOST} %{WORD:pihole_context} %{NOTSPACE:pihole_response}",
    value: to_string($message.pihole_message),
    only_named_captures: false);
    set_fields(my_fields);
//    set_field("match_type", my_match_type);
    set_field("parse", "parse - Pi-hole Enhanced - Parsing Parsing pihole_result field");
end

rule "Parse - Pi-hole Enhanced - Parse pihole_response field for IPORHOST"
when
    $message.pihole_action == "query"
then
    let my_new_fields = grok(pattern: "%{IPORHOST}",
    value: to_string($message.pihole_response),
    only_named_captures: false);
    set_fields(my_new_fields);
    set_field("parse","parse - Pi-hole Enhanced - Parse pihole_response field for IPORHOST");
end

rule "Parse - Pi-hole Enhanced - Splitting into additional fields"
when
    has_field("pihole_message") &&
    grok (pattern: "%{WORD:match_type} %{WORD:pihole_action} %{GREEDYDATA:pihole_response}",
         value: to_string($message.pihole_message)
         ).matches == true
//In the event the field pihole_message is present and the grok pattern matches,
//we run the following code below
then
    let my_fields = grok(pattern: "%{NOTSPACE:pihole_action} %{GREEDYDATA:pihole_result}",
    value: to_string($message.pihole_message),
    only_named_captures: false);
    set_fields(my_fields);
    set_field("parse", "parse - Pi-hole Enhanced - Splitting into additional fields");
end

rule "Parse - Pi-hole Enhanced - Parse for internal_ip again"
when
    $message.pihole_context =="is" && 
    grok (pattern: "%{IP}",
         value: to_string($message.pihole_response)
         ).matches == true && $message.IP != "0.0.0.0"
then
    set_field("internal_ip", false);
    set_field("parse","Parse - Pi-hole Enhanced - Parse for internal_ip again");
end

rule "Parse - Pi-hole Enhanced - Parse pihole_response field"
when
    has_field("pihole_response") &&
    grok (pattern: "%{IP}",
         value: to_string($message.pihole_response)
         ).matches == true
then
    let my_new_ip = to_ip($message.pihole_response);
    set_field("IP", my_new_ip);
    set_field("parse","parse - Pi-hole Enhanced - Parse pihole_response field");
end

rule "Parse - Pi-hole Enhanced - Parsing for 'query' in pihole_action"
when
    $message.pihole_action == "query"
then
//    let my_match_type = to_string($message.pihole_action);
    let my_fields = grok(pattern: "%{NOTSPACE:query_request} %{WORD:pihole_context} %{IPORHOST:pihole_response}",
    value: to_string($message.pihole_result),
    only_named_captures: true);
    set_fields(my_fields);
//    set_field("match_type", my_match_type);
    set_field("parse", "Parse - Pi-hole Enhanced - Parsing for 'query' in pihole_action");
end

rule "NML - Pi-hole Enhanced - NML pihole_action one last time"
when
    $message.pihole_action == "config"
then
    set_field("pihole_action", "reply");
    set_field("match_type", "config");
    set_field("parse", "NML - Pi-hole Enhanced - NML pihole_action one last time");
end

rule "Parse - Pi-hole Enhanced - Split messages into initial fields"
when
    grok(pattern: "%{PIHOLE}",
    value: to_string($message.message)
    ).matches == true
then
    let my_fields = grok(pattern: "%{PIHOLE} %{GREEDYDATA:pihole_message}",
    value: to_string($message.message),
    only_named_captures: false);

    set_fields(my_fields);
    set_field("parse", "parse - Pi-hole Enhanced - Setting initial fields");
end

rule "Parse - Pi-hole Enhanced - Parse for additional fields in query_action"
when
    $message.pihole_action == "query" && has_field("IP")
then
    let clean_ip = to_ip($message.IP);
    set_field("source_ip", clean_ip);
    set_field("parse", "Parse - Pi-hole Enhanced- Parse for additional fields in query_action");
end

rule "NML - Pi-hole Enhanced - NML for pihole_action again"
when
    has_field("pihole_message") && contains(to_string($message.pihole_message),
    "/etc/pihole/custom.list")
then
    set_field("pihole_action", "reply");
    set_field("match_type", "custom-list");
    set_field("parse", "NML - Pi-hole Enhanced - NML for pihole_action again");
end

rule "Parse - FW/WFW - Split into additional fields"
when
    grok (pattern: "%{WINFWMATCH}",
         value: to_string($message.message)
         ).matches == true
then
    let my_fields = grok(pattern: "%{WINFWMATCH}",
    value: to_string($message.message),
    only_named_captures: true);
    set_fields(my_fields);
    route_to_stream("Firewall Events Lab");
    set_field("Parse","Parse - FW/WFW - Split into additional fields");
end

rule "Parse - FW/OPN - Split into initial fields"
when
    grok (pattern: "%{FWLAB}",
         value: to_string($message.message)
         ).matches == true
then
    let my_fields = grok(pattern: "%{FWLAB}",
    value: to_string($message.message),
    only_named_captures: true);
    set_fields(my_fields);
    set_field("parse","Parse - FW/OPN - Split into initial fields");
end
