rule "Parse - Firewall - GROK"
when
  ( has_field("route") &&
to_string($message."route") == "Desktop Firewall - MS Logs"
)

then
  let gl2_fragment_grok_results = grok(
  pattern: "%{TIMESTAMP_ISO8601:event_timestamp} %{NOTSPACE:action} %{NOTSPACE:protocol} %{IP:source_ip} %{IP:destination_ip}( %{INT:source_port} %{INT:destination_port})? %{GREEDYDATA} %{WORD:path}",
  value: to_string($message."message"),
  only_named_captures: true
);
set_fields(
  fields: gl2_fragment_grok_results
);
  set_field(
    field : "parse",
    value : "Firewall - GROK",
    clean_field : false
  );
end

rule "Route - Desktop Firewall - MS Logs"
when
  grok(
  value: to_string($message."message"),
  pattern: "%{TIMESTAMP_ISO8601:event_timestamp} %{NOTSPACE:action} %{NOTSPACE:protocol} %{IP:sre_ip} %{IP:dst_ip}( %{INT:src_port} %{INT:dst_port})? %{GREEDYDATA} %{WORD:path}"
).matches == true
then
  route_to_stream(
    name : "Desktop Firewall Events",
    remove_from_default : true
  );
  set_field(
    field : "route",
    value : "Desktop Firewall - MS Logs"
  );
end
