source.@value:
rule "Enrich - Source - Threat Intelligence"
when
    has_field("source_ip") && 
    $message.source_is_external == true
then

//Grab Geo Details
let geo = lookup(
    lookup_table: "geo-ip",
    key: to_string($message.source_ip)
);

//Set fields with enriched details
set_field("source_geo_city_name",geo["city"].names.en);
set_field("source_geo_country_name",geo["country"].names.en);
set_field("source_geo_coordinates",geo["coordinates"]);
set_field("Enriched","Source - Threat Intelligence");

//Check for threat on IP
let intel = lookup(
    lookup_table: "greynoise-lookup",
    key: to_string($message.source_ip)
);

//Set classification Detail from Graynoise
set_field("threat_classification",intel["classification"]);

end

rule "Context - Threat Info - Destination"
when
    $message.threat_classification == "malicious" &&
    $message.destination_is_external == true
then
    set_field("threat_detected",true);
    set_field("threat_info_url",concat(
        first: "https://www.greynoise.io/viz/ip/",
        second: to_string($message.destination_ip)
            )
        );
end

