rule "Enrich - Destination - Asset Management"
when
    has_field("destination_ip") && 
    $message.destination_is_internal == true
then

let assetdata = lookup(
    lookup_table: "asset",
    key: to_string($message.destination_ip)
    );
set_fields(
    fields: assetdata,
    prefix: "destination_");
set_field("lookup","[Destination - Asset Management]");
end

