rule "Parse - Firewall - GROK"

when

    true

then

let parsed = grok(

    pattern:"%{TIMESTAMP_ISO8601:event_timestamp} %{NOTSPACE:action} %{NOTSPACE:protocol} %{IP:source_ip} %{IP:destination_ip}( %{INT:source_port})?( %{INT:destination_port})? %{GREEDYDATA} %{WORD:path}",

    value: to_string($message.message),

    only_named_captures: true
    );

    set_fields(
    fields:parsed
    );

    set_field(
    field: "Parse",
    value: "Firewall - GROK",
    prefix:"Awesome_"
    );

end

