rule "Parse - Apache"
when
    has_field("log_pack") &&
    to_string($message.log_pack) == "002-gl.json"
then
 let parsegrok = grok(
        pattern: "%{COMBINEDAPACHELOG}",
        value: to_string($message.message), 
        only_named_captures: true
        ); 
 set_fields(parsegrok); 
    set_field(
        field: "parser", 
        value: "apache_grok"
    );
set_field(
    field: "timestamp",
    value: now()
    );
set_field(
    field: "cluster_node",
    value: to_string($message.filebeat_host_name)
    );
    remove_field("filebeat_@timestamp");
    remove_field("filebeat_@timestamp");
    remove_field("filebeat_@metadata_beat");
remove_field("filebeat_@metadata_pipeline");
remove_field("filebeat_@metadata_type");
remove_field("filebeat_@metadata_version");
remove_field("filebeat_@timestamp");
remove_field("filebeat_agent_ephemeral_id");
remove_field("filebeat_agent_hostname");
remove_field("filebeat_agent_id");
remove_field("filebeat_agent_name");
remove_field("filebeat_agent_type");
remove_field("filebeat_agent_version");
remove_field("filebeat_ecs_version");
remove_field("filebeat_event_dataset");
remove_field("filebeat_event_module");
remove_field("filebeat_fileset_name");
remove_field("filebeat_host_architecture");
remove_field("filebeat_host_hostname");
remove_field("filebeat_host_id");


end

rule "Parse - UTM-KV"
when
    to_string($message.log_category) == "UTM"
then
    let parsekv = key_value(
        value: to_string($message.message),
        trim_value_chars: '"', 
        ignore_empty_values: true
        );
    set_fields(parsekv);
    set_field(
        field: "parser", 
        value: "Parse - UTM-KV"
    );
end
